.chrome-url {
  flex: 1;
  background: rgba(255, 255, 255, 0.05);
  padding: 4px 12px;
  border-radius: 6px;
  font-size: 0.75rem;
  color: var(--text-muted);
  text-align: center;
}

.theme-toggle {
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 50%;
  font-size: 0.9rem;
  cursor: pointer;
  transition: var(--transition);
}

.theme-toggle:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: scale(1.1);
}

.mockup-scroll {
  height: 400px;
  overflow-y: auto;
  scroll-behavior: smooth;
}

.mockup-scroll::-webkit-scrollbar {
  width: 4px;
}

.mockup-scroll::-webkit-scrollbar-track {
  background: rgba(0, 0, 0, 0.2);
}

.mockup-scroll::-webkit-scrollbar-thumb {
  background: rgba(255, 255, 255, 0.2);
  border-radius: 4px;
}

.mockup-scroll::-webkit-scrollbar-thumb:hover {
  background: rgba(255, 255, 255, 0.3);
}

.mockup-content {
  display: flex;
  flex-direction: column;
  gap: 0;
  transition: var(--transition);
}

/* Light Theme */
.light-theme {
  background: #f9fafb;
}

.light-theme .mock-section-wrapper {
  background: #ffffff;
  border-bottom: 1px solid #e5e7eb;
}

.light-theme .section-title-bar {
  background: linear-gradient(90deg, #2563eb, #06b6d4);
}

.light-theme .mock-card,
.light-theme .feature-box,
.light-theme .module,
.light-theme .team-member,
.light-theme .blog-item,
.light-theme .faq-item {
  background: #f9fafb;
  border: 1px solid #e5e7eb;
}

.light-theme .text-line,
.light-theme .card-title,
.light-theme .feature-title,
.light-theme .module-title,
.light-theme .member-name,
.light-theme .blog-title,
.light-theme .faq-question {
  background: #d1d5db;
}

.light-theme .card-icon,
.light-theme .feature-icon,
.light-theme .module-icon {
  background: #dbeafe;
  border: 2px solid #3b82f6;
}

.light-theme .mock-button,
.light-theme .form-button {
  background: linear-gradient(135deg, #2563eb, #06b6d4);
}

.light-theme .about-image,
.light-theme .gallery-item,
.light-theme .member-photo,
.light-theme .blog-image {
  background: linear-gradient(135deg, #dbeafe, #e0f2fe);
  border: 1px solid #bfdbfe;
}

.light-theme .mock-map {
  background: #dcfce7;
  border: 1px solid #86efac;
}

/* Dark Theme */
.dark-theme {
  background: #0a0b13;
}

.dark-theme .mock-section-wrapper {
  background: #111827;
  border-bottom: 1px solid #1f2937;
}

.dark-theme .section-title-bar {
  background: linear-gradient(90deg, #3b82f6, #06b6d4);
}

.dark-theme .mock-card,
.dark-theme .feature-box,
.dark-theme .module,
.dark-theme .team-member,
.dark-theme .blog-item,
.dark-theme .faq-item {
  background: #1f2937;
  border: 1px solid #374151;
}

.dark-theme .text-line,
.dark-theme .card-title,
.dark-theme .feature-title,
.dark-theme .module-title,
.dark-theme .member-name,
.dark-theme .blog-title,
.dark-theme .faq-question {
  background: #4b5563;
}

.dark-theme .card-icon,
.dark-theme .feature-icon,
.dark-theme .module-icon {
  background: rgba(59, 130, 246, 0.2);
  border: 2px solid #3b82f6;
}

.dark-theme .mock-button,
.dark-theme .form-button {
  background: linear-gradient(135deg, #3b82f6, #06b6d4);
}

.dark-theme .about-image,
.dark-theme .gallery-item,
.dark-theme .member-photo,
.dark-theme .blog-image {
  background: linear-gradient(135deg, rgba(59, 130, 246, 0.2), rgba(6, 182, 212, 0.2));
  border: 1px solid rgba(59, 130, 246, 0.3);
}

.dark-theme .mock-map {
  background: rgba(16, 185, 129, 0.2);
  border: 1px solid rgba(16, 185, 129, 0.3);
}

/* Section Wrappers */
.mock-section-wrapper {
  padding: 40px 20px;
  min-height: 200px;
}

.section-header {
  text-align: center;
  margin-bottom: 32px;
}

.section-title-bar {
  width: 120px;
  height: 8px;
  margin: 0 auto;
  border-radius: 4px;
  opacity: 0.8;
}

/* Hero Section */
.hero-section {
  min-height: 300px;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
}

.mock-hero {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
  position: relative;
  z-index: 2;
  width: 100%;
}

.hero-background {
  position: absolute;
  inset: 0;
  opacity: 0.1;
  z-index: 1;
}

.hero-background.gradient {
  background: linear-gradient(135deg, #3b82f6, #06b6d4, #10b981);
}

.mock-logo {
  width: 50px;
  height: 50px;
  background: rgba(249, 250, 251, 0.2);
  border-radius: 12px;
  border: 2px solid rgba(249, 250, 251, 0.3);
}

.mock-title {
  width: 60%;
  height: 28px;
  background: rgba(249, 250, 251, 0.3);
  border-radius: 6px;
}

.mock-subtitle {
  width: 70%;
  height: 18px;
  background: rgba(249, 250, 251, 0.2);
  border-radius: 4px;
}

.mock-button {
  width: 140px;
  height: 44px;
  border-radius: 8px;
  margin-top: 8px;
}

/* About Section */
.about-section {
  padding: 50px 20px;
}

.mock-about {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 24px;
  align-items: center;
}

.about-text {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.text-line {
  height: 12px;
  border-radius: 4px;
}

.text-line.short {
  width: 70%;
}

.about-image {
  width: 100%;
  height: 140px;
  border-radius: 12px;
}

/* Services Section */
.services-section {
  padding: 50px 20px;
}

.mock-cards {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 16px;
}

.mock-card {
  border-radius: 12px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 12px;
  align-items: center;
  text-align: center;
  transition: var(--transition);
}

.mock-card:hover {
  transform: translateY(-4px);
}

.card-icon {
  width: 40px;
  height: 40px;
  border-radius: 50%;
}

.card-title {
  width: 80%;
  height: 12px;
  border-radius: 4px;
}

.card-text {
  width: 100%;
  height: 8px;
  background: rgba(249, 250, 251, 0.1);
  border-radius: 4px;
}

/* Features Section */
.features-section {
  padding: 50px 20px;
}

.mock-features {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
}

.feature-box {
  border-radius: 12px;
  padding: 24px;
  display: flex;
  flex-direction: column;
  gap: 12px;
  align-items: center;
}

.feature-icon {
  width: 50px;
  height: 50px;
  border-radius: 12px;
}

.feature-title {
  width: 70%;
  height: 14px;
  border-radius: 4px;
}

.feature-desc {
  width: 90%;
  height: 8px;
  background: rgba(249, 250, 251, 0.1);
  border-radius: 4px;
}

/* Gallery Section */
.gallery-section {
  padding: 50px 20px;
}

.mock-gallery {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 12px;
}

.gallery-item {
  height: 80px;
  border-radius: 8px;
  transition: var(--transition);
}

.gallery-item.large {
  grid-column: span 2;
  grid-row: span 2;
  height: 172px;
}

.gallery-item:hover {
  transform: scale(1.05);
}

/* Map Section */
.map-section {
  padding: 50px 20px;
}

.mock-map {
  height: 180px;
  border-radius: 12px;
  position: relative;
  overflow: hidden;
}

.map-marker {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 24px;
  height: 24px;
  background: #ef4444;
  border-radius: 50% 50% 50% 0;
  transform: rotate(-45deg) translate(-50%, -50%);
  border: 3px solid white;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
}

.map-info {
  position: absolute;
  bottom: 16px;
  left: 16px;
  width: 120px;
  height: 40px;
  background: white;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
}

/* Contact Section */
.contact-section {
  padding: 50px 20px;
}

.mock-contact {
  max-width: 500px;
  margin: 0 auto;
}

.contact-form {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.form-field {
  height: 44px;
  background: rgba(249, 250, 251, 0.1);
  border: 1px solid rgba(249, 250, 251, 0.2);
  border-radius: 8px;
}

.form-field.large {
  height: 100px;
}

.form-button {
  height: 48px;
  border-radius: 8px;
  margin-top: 8px;
}

