  background: rgba(17, 24, 39, 0.9);
  border: 1px solid var(--border-color);
  border-radius: 24px;
  padding: 32px;
  position: relative;
  cursor: pointer;
  transition: var(--transition);
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
}

.template-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.5);
}

.template-card.featured {
  border-color: var(--premium-purple);
}

.card-glow {
  position: absolute;
  inset: 0;
  opacity: 0;
  transition: opacity 0.5s ease;
  z-index: 0;
  border-radius: 24px;
}

.template-card:hover .card-glow {
  opacity: 0.1;
}

.basic-glow {
  background: radial-gradient(circle at 50% 50%, var(--success-green), transparent 70%);
}

.avance-glow {
  background: radial-gradient(circle at 50% 50%, var(--primary-blue), transparent 70%);
}

.premium-glow {
  background: radial-gradient(circle at 50% 50%, var(--premium-purple), transparent 70%);
}

.popular-ribbon {
  position: absolute;
  top: 20px;
  right: -30px;
  background: linear-gradient(135deg, var(--premium-purple), var(--warning-amber));
  color: white;
  padding: 4px 40px;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 0.05em;
  transform: rotate(45deg);
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
  z-index: 10;
}

/* Template Header */
.template-header {
  position: relative;
  z-index: 1;
  margin-bottom: 24px;
  text-align: center;
}

.template-level {
  margin-bottom: 12px;
}

.level-badge {
  display: inline-block;
  padding: 6px 16px;
  border-radius: 20px;
  font-size: 0.75rem;
  font-weight: 700;
  letter-spacing: 0.05em;
}

.basic-badge {
  background: rgba(16, 185, 129, 0.2);
  color: var(--success-green);
  border: 1px solid rgba(16, 185, 129, 0.3);
}

.avance-badge {
  background: rgba(37, 99, 235, 0.2);
  color: var(--primary-blue);
  border: 1px solid rgba(37, 99, 235, 0.3);
}

.premium-badge {
  background: rgba(124, 58, 237, 0.2);
  color: var(--premium-purple);
  border: 1px solid rgba(124, 58, 237, 0.3);
}

.template-name {
  font-size: 2rem;
  font-weight: 700;
  color: var(--text-primary);
  margin-bottom: 8px;
}

.template-price {
  font-size: 2.5rem;
  font-weight: 700;
  color: var(--accent-cyan);
  margin-bottom: 8px;
  font-family: monospace;
}

.template-tagline {
  font-size: 0.95rem;
  color: var(--text-secondary);
  margin-bottom: 24px;
}

/* Preview Mockup */
.preview-mockup {
  position: relative;
  z-index: 1;
  margin-bottom: 24px;
  background: #1a1d2a;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.4);
}

.browser-chrome {
  background: #0a0b13;
  padding: 10px 12px;
  display: flex;
  align-items: center;
  gap: 12px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.chrome-dots {
  display: flex;
  gap: 6px;
}

.chrome-dots span {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: var(--text-muted);
}

.chrome-dots span:nth-child(1) { background: #ff5f56; }
.chrome-dots span:nth-child(2) { background: #ffbd2e; }
.chrome-dots span:nth-child(3) { background: #27c93f; }

.chrome-url {
  flex: 1;
  background: rgba(255, 255, 255, 0.05);
  padding: 4px 12px;
  border-radius: 6px;
  font-size: 0.75rem;
  color: var(--text-muted);
  text-align: center;
}

.mockup-content {
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 16px;
  min-height: 300px;
}

/* Mock Elements */
.mock-hero {
  background: rgba(37, 99, 235, 0.1);
  border-radius: 8px;
  padding: 32px 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 12px;
}

.mock-title {
  width: 60%;
  height: 24px;
  background: rgba(249, 250, 251, 0.2);
  border-radius: 4px;
}

.mock-subtitle {
  width: 80%;
  height: 16px;
  background: rgba(249, 250, 251, 0.15);
  border-radius: 4px;
}

.mock-button {
  width: 120px;
  height: 36px;
  background: var(--primary-blue);
  border-radius: 8px;
  margin-top: 8px;
}

.mock-section {
  padding: 12px;
}

.mock-cards {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 12px;
}

.mock-card {
  background: rgba(249, 250, 251, 0.1);
  border-radius: 6px;
  height: 60px;
  border: 1px solid rgba(249, 250, 251, 0.1);
}

.mock-contact {
  background: rgba(6, 182, 212, 0.1);
  border-radius: 8px;
  padding: 16px;
}

.mock-form {
  width: 100%;
  height: 80px;
  background: rgba(249, 250, 251, 0.1);
  border-radius: 6px;
}

/* Avanc√© Mockup */
.animated-hero {
  position: relative;
  overflow: hidden;
}

.shimmer {
  position: relative;
  overflow: hidden;
}

.shimmer::after {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  transform: translateX(-100%);
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  animation: shimmer 2s infinite;
}

@keyframes shimmer {
  100% {
    transform: translateX(100%);
  }
}

.pulse {
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.7;
  }
}

.mock-features {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 12px;
}

.feature-box {
  background: rgba(6, 182, 212, 0.15);
  border-radius: 6px;
  height: 60px;
  border: 1px solid rgba(6, 182, 212, 0.2);
}

.mock-gallery {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 8px;
}

.gallery-item {
  background: rgba(249, 250, 251, 0.1);
  border-radius: 6px;
  height: 50px;
  border: 1px solid rgba(249, 250, 251, 0.1);
}

.mock-map {
  background: rgba(16, 185, 129, 0.1);
  border-radius: 8px;
  height: 60px;
  border: 1px solid rgba(16, 185, 129, 0.2);
}

/* Premium Mockup */
.premium-hero {
  background: linear-gradient(135deg, rgba(124, 58, 237, 0.15), rgba(37, 99, 235, 0.15));
}

.mock-modules {
  padding: 12px;
}

.module-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 10px;
}

.module {
  background: rgba(124, 58, 237, 0.15);
  border-radius: 6px;
  height: 50px;
  border: 1px solid rgba(124, 58, 237, 0.2);
}

.mock-blog {
  display: flex;
  gap: 12px;
}

.blog-item {
  flex: 1;
  background: rgba(249, 250, 251, 0.1);
  border-radius: 6px;
  height: 70px;
  border: 1px solid rgba(249, 250, 251, 0.1);
}

/* Template Features */
.template-features {
  position: relative;
  z-index: 1;
  margin-bottom: 24px;
}

.feature-item {
  display: flex;
  align-items: flex-start;
  gap: 10px;
  padding: 8px 0;
  color: var(--text-secondary);
  font-size: 0.9rem;
  line-height: 1.5;
}

.feature-icon {
  color: var(--success-green);
  font-weight: 700;
  font-size: 1rem;
  flex-shrink: 0;
}

.feature-text {
  flex: 1;
}

/* Template Meta */
.template-meta {
  display: flex;
  justify-content: center;
  gap: 24px;
  margin-bottom: 24px;
  padding: 16px;
  background: rgba(3, 7, 18, 0.5);
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.meta-item {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 0.9rem;
  color: var(--text-secondary);
}

.meta-icon {
  font-size: 1.2rem;
}

/* Template CTA */
.template-cta {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  padding: 14px 24px;
  border-radius: 12px;
  font-weight: 600;
  border: none;
  cursor: pointer;
  transition: var(--transition);
  position: relative;
  z-index: 1;
  overflow: hidden;
}

.basic-cta {
  background: linear-gradient(135deg, var(--success-green), var(--accent-cyan));
  color: white;
}

.avance-cta {
  background: linear-gradient(135deg, var(--primary-blue), var(--accent-cyan));
  color: white;
}

.premium-cta {
  background: linear-gradient(135deg, var(--premium-purple), var(--warning-amber));
  color: white;
}

.template-cta:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.3);
}

.cta-arrow {
  transition: transform 0.3s ease;
}

.template-cta:hover .cta-arrow {
  transform: translateX(4px);
}

/* Comparison Table */
.comparison-section {
  max-width: 1000px;
  margin: 0 auto 80px;
}

.comparison-table {
  background: rgba(17, 24, 39, 0.6);
  border: 1px solid var(--border-color);
  border-radius: 16px;
  padding: 32px;
  backdrop-filter: blur(10px);
}

.comparison-header,
.comparison-row {
  display: grid;
  grid-template-columns: 200px repeat(3, 1fr);
  gap: 16px;
  padding: 12px;
  align-items: center;
}

.comparison-header {
  background: rgba(37, 99, 235, 0.1);
  border-radius: 8px;
  margin-bottom: 8px;
  font-weight: 600;
}

.comparison-row {
  border-bottom: 1px solid var(--border-color);
}

.comparison-row:last-child {
  border-bottom: none;
}

.comp-label {
  color: var(--text-primary);
  font-weight: 500;
}

.comp-col {
  text-align: center;
  color: var(--text-secondary);
  font-weight: 500;
}

/* WebApp Section */
.webapp-section {
  max-width: 1200px;
  margin: 0 auto 80px;
}

.webapp-card {
  background: rgba(17, 24, 39, 0.9);
  border: 1px solid var(--border-color);
  border-radius: 24px;
  padding: 40px;
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.3);
}

.webapp-title {
  font-size: 2rem;
  color: var(--text-primary);
  margin-bottom: 12px;
  font-weight: 700;
}

.webapp-price {
  font-size: 1.5rem;
  color: var(--accent-cyan);
  font-weight: 700;
  margin-bottom: 16px;
  font-family: monospace;
}

.webapp-description {
  color: var(--text-secondary);
  font-size: 1.05rem;
  line-height: 1.7;
  margin-bottom: 32px;
}

.webapp-features {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 24px;
  margin-bottom: 32px;
}

.webapp-col h4 {
  color: var(--text-primary);
  font-size: 1.1rem;
  margin-bottom: 12px;
  font-weight: 600;
}

.webapp-col ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.webapp-col li {
  padding: 6px 0;
  color: var(--text-secondary);
  font-size: 0.9rem;
}

.webapp-col li::before {
  content: '‚ñ∏';
  color: var(--accent-cyan);
  margin-right: 8px;
  font-weight: bold;
}

.webapp-pricing {
  background: rgba(3, 7, 18, 0.5);
  border-radius: 12px;
  padding: 24px;
  margin-bottom: 32px;
  border: 1px solid var(--border-color);
}

.pricing-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 0;
  border-bottom: 1px solid var(--border-color);
}

.pricing-item:last-child {
  border-bottom: none;
}

.pricing-duration {
  color: var(--text-secondary);
  font-size: 0.95rem;
}

.pricing-amount {
  color: var(--accent-cyan);
  font-weight: 700;
  font-size: 1.1rem;
  font-family: monospace;
}

.webapp-cta {
  display: inline-flex;
  align-items: center;
  gap: 12px;
  padding: 16px 32px;
  background: linear-gradient(135deg, var(--primary-blue), var(--accent-cyan));
  color: white;
  text-decoration: none;
  border-radius: 12px;
  font-weight: 600;
  font-size: 1.05rem;
  transition: var(--transition);
}

.webapp-cta:hover {
  transform: translateY(-3px);
  box-shadow: 0 12px 24px rgba(37, 99, 235, 0.4);
}

/* Portfolio */
.portfolio-section {
  max-width: 1200px;
  margin: 0 auto 80px;
}

.portfolio-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
  gap: 32px;
  margin-bottom: 40px;
}

.portfolio-item {
  background: rgba(17, 24, 39, 0.6);
  border: 1px solid var(--border-color);
  border-radius: 16px;
  overflow: hidden;
  text-decoration: none;
  transition: var(--transition);
  position: relative;
}

.portfolio-item:hover {
  transform: translateY(-8px);
  border-color: var(--primary-blue);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.4);
}

.portfolio-image {
  width: 100%;
  height: 200px;
  overflow: hidden;
}

.portfolio-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s ease;
}

.portfolio-item:hover .portfolio-image img {
  transform: scale(1.1);
}

.portfolio-info {
  padding: 20px;
}

.portfolio-title {
  font-size: 1.2rem;
  color: var(--text-primary);
  margin-bottom: 8px;
  font-weight: 600;
}

.portfolio-type {
  font-size: 0.9rem;
  color: var(--text-secondary);
  margin-bottom: 12px;
}

.portfolio-tags {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.portfolio-tag {
  background: rgba(6, 182, 212, 0.1);
  color: var(--accent-cyan);
  padding: 4px 10px;
  border-radius: 12px;
  font-size: 0.75rem;
  font-weight: 500;
  border: 1px solid rgba(6, 182, 212, 0.2);
}

.portfolio-link {
  position: absolute;
  bottom: 20px;
  right: 20px;
  color: var(--accent-cyan);
  font-weight: 600;
  font-size: 0.9rem;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.portfolio-item:hover .portfolio-link {
  opacity: 1;
}

.portfolio-cta {
  text-align: center;
}

.all-projects-btn {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 14px 28px;
  background: linear-gradient(135deg, var(--primary-blue), var(--accent-cyan));
  color: white;
  text-decoration: none;
  border-radius: 12px;
  font-weight: 600;
  transition: var(--transition);
}

.all-projects-btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 16px rgba(37, 99, 235, 0.3);
}

/* Modal */
.modal-backdrop {
  position: fixed;
  inset: 0;
  background: rgba(3, 7, 18, 0.95);
  backdrop-filter: blur(10px);
  z-index: 2000;
  animation: fadeIn 0.3s ease;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.template-modal {
  position: fixed;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 2001;
  padding: 20px;
}

.modal-wrapper {
  max-width: 800px;
  width: 100%;
  max-height: 90vh;
  background: rgba(17, 24, 39, 0.98);
  border: 1px solid var(--border-color);
  border-radius: 24px;
  position: relative;
  animation: slideUp 0.4s ease;
  overflow: hidden;
  box-shadow: 0 25px 50px rgba(0, 0, 0, 0.5);
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.modal-close {
  position: absolute;
  top: 20px;
  right: 20px;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid var(--border-color);
  color: var(--text-primary);
  font-size: 1.5rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: var(--transition);
  z-index: 10;
}

.modal-close:hover {
  background: rgba(239, 68, 68, 0.2);
  border-color: #ef4444;
  color: #ef4444;
  transform: rotate(90deg);
}

.modal-content {
  overflow-y: auto;
  max-height: 90vh;
}

.modal-header {
  padding: 40px 40px 20px;
  border-bottom: 1px solid var(--border-color);
}

.modal-header.basic-theme {
  background: linear-gradient(135deg, rgba(16, 185, 129, 0.1), rgba(6, 182, 212, 0.1));
}

.modal-header.avance-theme {
  background: linear-gradient(135deg, rgba(37, 99, 235, 0.1), rgba(6, 182, 212, 0.1));
}

.modal-header.premium-theme {
  background: linear-gradient(135deg, rgba(124, 58, 237, 0.1), rgba(245, 158, 11, 0.1));
}

.modal-title {
  font-size: 2rem;
  color: var(--text-primary);
  margin-bottom: 12px;
  font-weight: 700;
}

.modal-price {
  font-size: 1.8rem;
  font-weight: 700;
  color: var(--accent-cyan);
  font-family: monospace;
}

.modal-body {
  padding: 40px;
}

.modal-section {
  margin-bottom: 32px;
}

.modal-section h3 {
  font-size: 1.2rem;
  color: var(--text-primary);
  margin-bottom: 16px;
  font-weight: 600;
}

.modal-features {
  list-style: none;
  padding: 0;
  margin: 0;
  display: grid;
  gap: 12px;
}

.modal-features li {
  display: flex;
  align-items: flex-start;
  gap: 12px;
  color: var(--text-secondary);
  font-size: 0.95rem;
  line-height: 1.6;
}

.check {
  color: var(--success-green);
  font-weight: 700;
  font-size: 1.1rem;
  flex-shrink: 0;
}

.modal-tech {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
}

.tech-badge {
  background: rgba(6, 182, 212, 0.1);
  color: var(--accent-cyan);
  padding: 8px 16px;
  border-radius: 16px;
  font-size: 0.9rem;
  font-weight: 600;
  border: 1px solid rgba(6, 182, 212, 0.2);
}

.modal-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 16px;
  margin-bottom: 32px;
}

.modal-info-item {
  background: rgba(3, 7, 18, 0.5);
  border: 1px solid var(--border-color);
  border-radius: 12px;
  padding: 20px;
  text-align: center;
}

.info-label {
  font-size: 0.85rem;
  color: var(--text-muted);
  margin-bottom: 8px;
}

.info-value {
  font-size: 1.2rem;
  color: var(--text-primary);
  font-weight: 600;
}

.modal-cta {
  text-align: center;
}

.modal-btn {
  display: inline-flex;
  align-items: center;
  gap: 12px;
  padding: 16px 32px;
  color: white;
  text-decoration: none;
  border-radius: 12px;
  font-weight: 600;
  font-size: 1.05rem;
  transition: var(--transition);
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.3);
}

.basic-btn {
  background: linear-gradient(135deg, var(--success-green), var(--accent-cyan));
}

.avance-btn {
  background: linear-gradient(135deg, var(--primary-blue), var(--accent-cyan));
}

.premium-btn {
  background: linear-gradient(135deg, var(--premium-purple), var(--warning-amber));
}

.modal-btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 12px 24px rgba(0, 0, 0, 0.4);
}

/* Responsive */
@media (max-width: 1024px) {
  .templates-grid {
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  }
}

@media (max-width: 768px) {
  .services-container {
    padding: 20px 16px;
  }

  .main-title {
    font-size: 2rem;
    flex-direction: column;
  }

  .main-subtitle {
    font-size: 1rem;
  }

  .templates-grid {
    grid-template-columns: 1fr;
  }

  .comparison-header,
  .comparison-row {
    grid-template-columns: 1fr;
    gap: 8px;
  }

  .comp-col {
    text-align: left;
    padding-left: 20px;
  }

  .webapp-features {
    grid-template-columns: 1fr;
  }

  .modal-header {
    padding: 30px 20px 20px;
  }

  .modal-title {
    font-size: 1.5rem;
  }

  .modal-body {
    padding: 20px;
  }

  .modal-grid {
    grid-template-columns: 1fr;
  }

  .mock-cards,
  .mock-features {
    grid-template-columns: repeat(2, 1fr);
  }

  .module-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (max-width: 480px) {
  .template-card {
    padding: 24px;
  }

  .template-name {
    font-size: 1.5rem;
  }

  .template-price {
    font-size: 2rem;
  }

  .webapp-card {
    padding: 24px;
  }

  .modal-wrapper {
