import { createRouter, createWebHistory } from 'vue-router';
import HomePage from '@/components/HomePage.vue';
import UserProfile from '@/views/UserProfile.vue';
import ProjectsPage from '@/views/Projets.vue';
import ServicesPage from '@/views/Services.vue';
import ContactPage from '@/views/Contact.vue';
import NotFoundPage from '@/views/NotFound.vue';

const routes = [
  {
    path: '/',
    name: 'HomePage',
    component: HomePage,
    meta: {
      title: 'Amine ITJI - Master 2 IA & Développeur',
      description: 'Portfolio d\'Amine ITJI, étudiant en Master 2 Intelligence Artificielle et développeur freelance spécialisé en IA et développement web.'
    }
  },
  {
    path: '/profil',
    name: 'UserProfile',
    component: UserProfile,
    meta: {
      title: 'À propos - Amine ITJI',
      description: 'Découvrez mon parcours, mes compétences et mon expérience en Intelligence Artificielle et développement web.'
    }
  },
  {
    path: '/projets',
    name: 'ProjectsPage',
    component: ProjectsPage,
    meta: {
      title: 'Mes Projets - Amine ITJI',
      description: 'Explorez mes réalisations en IA, machine learning, développement web et projets de recherche.'
    }
  },
  {
    path: '/services',
    name: 'ServicesPage',
    component: ServicesPage,
    meta: {
      title: 'Mes Services - Amine ITJI',
      description: 'Découvrez mes services de développement web, applications sur mesure et solutions IA pour votre entreprise.'
    }
  },
  {
    path: '/contact',
    name: 'ContactPage',
    component: ContactPage,
    meta: {
      title: 'Contact - Amine ITJI',
      description: 'Contactez-moi pour discuter de vos projets en IA, développement web ou pour un stage de fin d\'études.'
    }
  },
  // Redirections pour l'ancien routing
  {
    path: '/projets-universitaires',
    redirect: '/projets?filter=univ'
  },
  {
    path: '/projets-personnels',
    redirect: '/projets?filter=perso'
  },
  {
    path: '/commandez',
    redirect: '/services'
  },
  // Page 404
  {
    path: '/:pathMatch(.*)*',
    name: 'NotFoundPage',
    component: NotFoundPage,
    meta: {
      title: 'Page non trouvée - Amine ITJI'
    }
  }
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
  scrollBehavior(to, from, savedPosition) {
    if (savedPosition) {
      return savedPosition;
    } else if (to.hash) {
      return {
        el: to.hash,
        behavior: 'smooth'
      };
    } else {
      return { top: 0, behavior: 'smooth' };
    }
  }
});

// Gestion des méta-données pour le SEO
router.beforeEach((to, from, next) => {
  // Mise à jour du titre de la page
  if (to.meta && to.meta.title) {
    document.title = to.meta.title;
  }

  // Mise à jour de la description
  if (to.meta && to.meta.description) {
    let descriptionMeta = document.querySelector('meta[name="description"]');
    if (!descriptionMeta) {
      descriptionMeta = document.createElement('meta');
      descriptionMeta.setAttribute('name', 'description');
      document.head.appendChild(descriptionMeta);
    }
    descriptionMeta.setAttribute('content', to.meta.description);
  }

  // Gestion des filtres dans l'URL pour la page projets
  if (to.name === 'Projets' && to.query.filter) {
    // Le composant Projets gérera le filtre via les query params
  }

  next();
});

export default router;
